VALUES ('Offer Poll', 'offer-poll-2', '<p id="">We‚Äôve moved from the world of information to the world of recommendation. Your customers can search for homes on Zillow, Redfin, and thousands of other sites but what they can‚Äôt find is a simple list of the best homes for sale.&nbsp;</p><p id="">‚Äç</p><p id="">Drop this poll on Instagram and start generating leads today.</p>', 'https://ik.imagekit.io/listingleads/Campaigns/Social%20Shareables/offer-poll-2/thumbnail-offer-poll-2.png', false, 'static_image', 'https://www.canva.com/design/DAGNiIghf80/5DkT-bl1p-kK8JMFco8Mvg/view?utm_content=DAGNiIghf80&utm_campaign=designshare&utm_medium=link&utm_source=publishsharelink&mode=preview', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, ARRAY['https://ik.imagekit.io/listingleads/Campaigns/Social%20Shareables/offer-poll-2/example-offer-poll-2.png'], NULL, 'US', false, true);

