VALUES ('Year Of Review Social', 'year-of-review-social', '<p id="">This is the time of the year to take your victory lap. You‚Äôve worked hard all year and it‚Äôs time to celebrate your milestones. This is a great opportunity to show off your results and express your gratitude.&nbsp;</p>', 'https://ik.imagekit.io/listingleads/Campaigns/Social%20Shareables/year-of-review-social/thumbnail-year-of-review-social.png', false, 'static_image', 'https://www.canva.com/design/DAGYz-1fWEU/pWtoI6FoT1BadkKRUR6zyQ/view?utm_content=DAGYz-1fWEU&utm_campaign=designshare&utm_medium=link&utm_source=publishsharelink&mode=preview', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, ARRAY['https://ik.imagekit.io/listingleads/Campaigns/Social%20Shareables/year-of-review-social/example-year-of-review-social.png'], '[{"title":"Customize Canva Templates to Match Your Brand","body":""},{"title":"Post to Instagram","body":"<p>Start a New Story: Open Instagram and create a new story. Upload Images (Slides) one by one and on the last slide add Poll.</p>"}]', 'US', false, true);INSERT INTO email_campaigns (name, slug, introduction, thumbnail_url, subject_line, email_body, preview_text, target_audience, tags, recommended_audience, example_image_url, full_email_image_url, execution_steps, region, is_featured, is_active)
VALUES ('$100M Email', '100m-email', '<p id="">This campaign alone has generated hundreds of listing opportunities.<br></p><p id="">It‚Äôs concise. It‚Äôs compelling. And it‚Äôs value-driven.<br></p><p id="">Pro-tip: Follow up with a personalized text to anyone who responds.<br></p><p id="">Don‚Äôt wait for permission to be useful. Send this email today. </p>', 'https://ik.imagekit.io/listingleads/Campaigns/Email/100m-email/thumbnail-100m-email.png', '<p id="">How much equity did you gain in 2024?</p>', '<p id="">I''m setting aside a few hours this week to put together equity reports for my clients.</p><p>‚Äç</p><p id="">These reports are more comprehensive and accurate than what you''d get from an online tool.</p><p>‚Äç</p><p id="">Can I send you one for your home?</p><p>‚Äç</p><p id="">Let me know!</p><p>‚Äç</p><p id="">Sincerely,</p><p id="">Jimmy</p>', NULL, 'Entire Database', ARRAY['past-clients', 'soi', 'direct-response'], NULL, NULL, 'https://ik.imagekit.io/listingleads/Campaigns/Email/100m-email/example-100m-email.png', '[{"title":"Copy Subject Line and Message to CRM","body":""},{"title":"Transferring Email Content to CRM","body":"<ol id=\"\"><li id=\"\">Copy Subject: Highlight and copy the <strong id=\"\">subject line</strong> from your email draft.</li><li id=\"\">Paste Subject: Open the email blast app and paste it into the <strong id=\"\">subject line</strong> field.</li><li id=\"\">Copy Email Content: Return to your draft, copy the <strong id=\"\">email content</strong>.</li><li id=\"\">Paste Content: Paste it in the <strong id=\"\">message body</strong> of the email blast app.</li><li id=\"\">Review and Send: Check for accuracy, then schedule or send the email.</li></ol><p>‚Äç</p>"}]', 'US', true, true);

