VALUES ('üö®Mortgage Rate DROP üö® (US)', 'mortgage-rate-drop', '<p id="">The #1 reason why your clients won''t sell their current home might be because they have a great interest rate. But as rates drop, they might be more open to the idea of making a move.</p><p id="">‚Äç</p><p id="">Let''s take the opportunity of rates dropping below 7% to engage our SOI and find our next listing.</p>', 'https://ik.imagekit.io/listingleads/Campaigns/Email/mortgage-rate-drop/thumbnail-mortgage-rate-drop.png', '<p id="">üö®Mortgage Rate DROP üö®</p>', '<p id="">I wanted to make sure you saw the news‚Äî</p><p id="">‚Äç</p><p id="">The average 30-year fixed mortgage rate today came in at 6.96%.</p><p id="">‚Äç</p><p id="">This is near the lowest rate we‚Äôve seen since late December‚Äîa promising sign.</p><p id="">‚Äç</p><p id="">I anticipate a lot of my clients will revisit their plans for the spring.</p><p id="">‚Äç</p><p id="">Do you think it will impact yours?</p>', NULL, 'Entire Database', ARRAY['past-clients', 'soi', 'direct-response'], 'Everyone', NULL, 'https://ik.imagekit.io/listingleads/Campaigns/Email/mortgage-rate-drop/example-mortgage-rate-drop.png', '[{"title":"Copy Subject Line and Message to CRM","body":""},{"title":"Transferring Email Content to CRM","body":"<ol id=\"\"><li id=\"\">Copy Subject: Highlight and copy the <strong id=\"\">subject line</strong> from your email draft.</li><li id=\"\">Paste Subject: Open the email blast app and paste it into the <strong id=\"\">subject line</strong> field.</li><li id=\"\">Copy Email Content: Return to your draft, copy the <strong id=\"\">email content</strong>.</li><li id=\"\">Paste Content: Paste it in the <strong id=\"\">message body</strong> of the email blast app.</li><li id=\"\">Review and Send: Check for accuracy, then schedule or send the email.</li></ol><p>‚Äç</p>"}]', 'US', false, true);INSERT INTO phone_text_scripts (name, slug, introduction, thumbnail_url, script_type, target_audience, text_message_1, text_message_2, text_message_3, call_script_pdf_url, call_script_content, example_image_url, full_script_image_url, execution_steps, region, is_featured, is_active)
VALUES ('1:1 Market Update', '1-1-market-update', '<p id="">How do you generate more listings?</p><p id="">Have more conversations.</p><p id="">Here''s how:</p><p id="">Professional agents study the market daily, but most don''t do anything with that insight unless asked.</p><p id="">Here''s what I want you to do:</p><p id="">The next time you review recent sales, text someone in your SOI who lives near that property.</p><p id="">This is a 1 to 1 market update that is‚Ä¶</p><p id="">Easy to digest.</p><p id="">Personalized.</p><p id="">Relevant.</p><p id="">When you get a response, provide them with all the information about the sale and offer a free home valuation report.</p><p id="">Start doing 5 of these a day and watch the magic happen. üöÄ</p>', 'https://ik.imagekit.io/listingleads/Campaigns/Text%20&%20Voice/1-1-market-update/thumbnail-1-1-market-update.png', 'text', NULL, '<p>Hi [First Name],</p><p>‚Äç</p><p>I&nbsp;was in the MLS&nbsp;earlier today and I just saw that 123 Main Street sold in your neighborhood.</p><p>‚Äç</p><p>Did you see the final sale price?</p>', NULL, NULL, NULL, NULL, NULL, 'https://ik.imagekit.io/listingleads/Campaigns/Text%20&%20Voice/1-1-market-update/example-1-1-market-update.png', '[{"title":"Copy the message content and send it via SMS or DM.","body":""},{"title":"Sending Your Message via Mass SMS or DM Tool","body":"<ol id=\"\"><li id=\"\">Log In to Your Mass Messaging Tool: Open your mass SMS or DM platform and log in to your account. (or simply use your phone)</li><li id=\"\">Select or Upload Recipients: Choose your audience (contacts/followers) or selecting recipients within the tool.</li><li id=\"\">Compose the Message: Paste the copied message content into the message field.</li><li id=\"\">Review and Send: Double-check the message for accuracy, then hit \"Send\" to distribute to all selected recipients.</li></ol>"}]', 'US', true, true);

