VALUES ('20% Spike In Mortgage Applications (US)', '20-spike-in-mortgage-applications-2', '<p id="">Mortgage demand just jumped 20% in a single week. That‚Äôs not just a number‚Äîit‚Äôs a reason to reach out.</p><p id="">Buyers are watching rates closely, and with more of them jumping back into the market, competition is heating up. This shift could mean stronger offers and better opportunities for sellers‚Äîbut only for those who are paying attention.</p><p id="">Now is the perfect time to check in with prospects and spark a conversation. A simple, easy-to-answer question like <em id="">‚ÄúHave you been keeping an eye on the market?‚Äù</em> can open the door to a meaningful discussion.</p><p id="">Send this text to 25-50 prospects.</p><p id="">‚Äç</p>', 'https://ik.imagekit.io/listingleads/Campaigns/Text%20&%20Voice/20-spike-in-mortgage-applications-2/thumbnail-20-spike-in-mortgage-applications-2.png', 'text', '25-50 Prospects', '<p id="">Hi Tom,</p><p id="">I just read that mortgage applications spiked 20% since rates have dropped.</p><p id="">Have you been following the market closely?</p>', NULL, NULL, NULL, NULL, NULL, 'https://ik.imagekit.io/listingleads/Campaigns/Text%20&%20Voice/20-spike-in-mortgage-applications-2/example-20-spike-in-mortgage-applications-2.png', '[{"title":"Copy the message content and send it via SMS or DM.","body":""},{"title":"Sending Your Message via Mass SMS or DM Tool","body":"<ol id=\"\"><li>Log In to Your Mass Messaging Tool: Open your mass SMS or DM platform and log in to your account. (or simply use your phone)</li><li>Select or Upload Recipients: Choose your audience (contacts/followers) or selecting recipients within the tool.</li><li>Compose the Message: Paste the copied message content into the message field.</li><li>Review and Send: Double-check the message for accuracy, then hit \"Send\" to distribute to all selected recipients.</li></ol>"}]', 'US', false, true);

INSERT INTO phone_text_scripts (name, slug, introduction, thumbnail_url, script_type, target_audience, text_message_1, text_message_2, text_message_3, call_script_pdf_url, call_script_content, example_image_url, full_script_image_url, execution_steps, region, is_featured, is_active)
VALUES ('3% Text', '3-text', '<p id="">You''ve probably got a database full of clients who are never going to sell their house.</p><p>‚Äç</p><p id="">Because if they were to buy their same house again today, their mortgage payment would be more than double.</p><p>‚Äç</p><p id="">It makes sense why you might think that there isn''t much value in staying in touch with these folks‚Ä¶</p><p>‚Äç</p><p id="">But that would be short-sighted.</p><p>‚Äç</p><p id="">Happy clients are your best referral sources.</p><p>‚Äç</p><p id="">Try this text message instead.</p>', 'https://ik.imagekit.io/listingleads/Campaigns/Text%20&%20Voice/3-text/thumbnail-3-text.png', 'text', NULL, '<p>Hi [First Name],</p><p>‚Äç</p><p>I&nbsp;thought you''d appreciate this update: Since you bought your house in [year], its value has increased by [%].</p><p>‚Äç</p><p>In fact, if you were to buy your house today, your mortgage payment would be [$] more per month.</p><p>‚Äç</p><p>You made a really smart financial decision buying when you did.</p><p>‚Äç</p><p>If you have any plans to make any improvements to the home, let me know. </p><p>‚Äç</p><p>I''ve got a great list of recommended vendors I can connect you with.</p><p>‚Äç</p><p>Thank you,</p><p>‚Äç</p><p>[Your Name]</p>', NULL, NULL, NULL, NULL, NULL, 'https://ik.imagekit.io/listingleads/Campaigns/Text%20&%20Voice/3-text/example-3-text.png', '[{"title":"Copy the message content and send it via SMS or DM.","body":""},{"title":"Sending Your Message via Mass SMS or DM Tool","body":"<ol id=\"\"><li id=\"\">Log In to Your Mass Messaging Tool: Open your mass SMS or DM platform and log in to your account. (or simply use your phone)</li><li id=\"\">Select or Upload Recipients: Choose your audience (contacts/followers) or selecting recipients within the tool.</li><li id=\"\">Compose the Message: Paste the copied message content into the message field.</li><li id=\"\">Review and Send: Double-check the message for accuracy, then hit \"Send\" to distribute to all selected recipients.</li></ol><p>‚Äç</p>"}]', 'US', false, true);

INSERT INTO phone_text_scripts (name, slug, introduction, thumbnail_url, script_type, target_audience, text_message_1, text_message_2, text_message_3, call_script_pdf_url, call_script_content, example_image_url, full_script_image_url, execution_steps, region, is_featured, is_active)
VALUES ('Buddy System CMA', 'buddy-system-cma', '<p id="">Let''s be honest‚Äîsometimes it can be easier to call your colleague''s past client list than it is to call your own.</p><p>‚Äç</p><p id="">So why not switch it up?</p><p>‚Äç</p><p id="">Using the Buddy System CMA strategy, swap lists with a colleague.</p><p>‚Äç</p><p id="">Here''s the exact script you can use.</p>', 'https://ik.imagekit.io/listingleads/Campaigns/Text%20&%20Voice/buddy-system-cma/thumbnail-buddy-system-cma.png', 'text', NULL, '<p id="">Hi [First Name],</p><p id="">‚Äç</p><p id="">This is [Your Name] with [Your Team]. I&nbsp;work with your agent [Name].</p><p id="">‚Äç</p><p id="">Did I&nbsp;catch you at a bad time?</p><p id="">‚Äç</p><p id="">The reason we''re reaching out today is because the market has been changing so much lately that we wanted to make sure that you had the most updated information about your home''s value.</p><p id="">‚Äç</p><p id="">When was the last time you checked your home''s value?</p><p>‚Äç</p><p>Have you made any upgrades to your property recently?</p><p>‚Äç</p><p>Ok great, I''ll pass this information to [Your Colleague] and they''ll get it over to you by tomorrow.</p><p>‚Äç</p><p>They''ll give you a call to walk through the main points. Friday at 3PM&nbsp;work?</p><p>‚Äç</p><p>One more thing‚Äîwhat plans do you have about the home that you think we should know about?</p>', NULL, NULL, NULL, NULL, NULL, 'https://ik.imagekit.io/listingleads/Campaigns/Text%20&%20Voice/buddy-system-cma/example-buddy-system-cma.png', '[{"title":"Copy the message content and send it via SMS or DM","body":""},{"title":"Sending Your Message via Mass SMS or DM Tool","body":"<ol id=\"\"><li id=\"\">Log In to Your Mass Messaging Tool: Open your mass SMS or DM platform and log in to your account. (or simply use your phone)</li><li id=\"\">Select or Upload Recipients: Choose your audience (contacts/followers) or selecting recipients within the tool.</li><li id=\"\">Compose the Message: Paste the copied message content into the message field.</li><li id=\"\">Review and Send: Double-check the message for accuracy, then hit \"Send\" to distribute to all selected recipients.</li></ol>"}]', 'US', false, true);

