VALUES ('Expired Marketing - Call Script 1', 'expired-marketing---call-script-1', '<p id="">This script changes the approach. It <strong id="">validates their frustration</strong> and shifts the conversation from <em id="">what went wrong</em> to <em id="">how to make it right.</em> Instead of pushing, it <strong id="">opens the door to a real dialogue</strong>‚Äîone that positions you as the agent who actually understands their challenges.</p><p id="">Use this script to disarm objections, build trust, and start conversations that lead to listings.</p>', 'https://ik.imagekit.io/listingleads/Campaigns/Text%20&%20Voice/expired-marketing---call-script-1/thumbnail-expired-marketing---call-script-1.png', 'text', 'Leads', NULL, NULL, NULL, NULL, NULL, NULL, 'https://ik.imagekit.io/listingleads/Campaigns/Text%20&%20Voice/expired-marketing---call-script-1/example-expired-marketing---call-script-1.png', NULL, 'US', false, true);

